-- ENDF Reports table and seed data
-- Scraped from https://www.nndc.bnl.gov/endfdocs/

CREATE TABLE IF NOT EXISTS endf_reports (
  id              BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  seq_number      INT NOT NULL,
  report_number   TEXT NOT NULL UNIQUE,
  authors         TEXT,
  title           TEXT NOT NULL,
  report_date     TEXT,
  cross_reference TEXT,
  pdf_url         TEXT,
  created_at      TIMESTAMPTZ DEFAULT NOW()
);

CREATE INDEX IF NOT EXISTS idx_endf_reports_seq ON endf_reports(seq_number);
CREATE INDEX IF NOT EXISTS idx_endf_reports_number ON endf_reports(report_number);

GRANT SELECT ON endf_reports TO anon;

